INSERT INTO `cbblab_addonarticle` VALUES ('108', '19', '<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px; text-indent: 25pt;\">\r\n	<span style=\"font-size: 12pt;\">天津大学是教育部直属国家重点大学，其前身为北洋大学，始建于1895年，是中国第一所现代大学，素以&ldquo;实事求是&rdquo;的校训、&ldquo;严谨治学&rdquo;的校风和&ldquo;爱国奉献&rdquo;的传统享誉海内外，是&ldquo;985工程&rdquo;、&ldquo;211工程&rdquo;、&ldquo;2011计划&rdquo;首批重点建设与支持的高水平研究型大学，学校师资力量雄厚、学科特色鲜明、教育质量和科研水平居于国内一流、在国际上具有较高影响力。　</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px; text-indent: 25pt;\">\r\n	<span style=\"font-size: 12pt;\">根据学校管理队伍发展规划和实际工作需要，本着公开、公平、公正的原则，现面向国内外招聘管理人员若干名，具体信息如下：</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px; text-indent: 18pt;\">\r\n	<span style=\"font-size: 12pt;\">招聘部门：天津大学各院级单位、各机关部处室</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px; text-indent: 18pt;\">\r\n	<span style=\"font-size: 12pt;\">招聘信息：详见天津大学人力资源招聘系统</span><a _fcksavedurl=\"http://121.193.130.41/rs_recruit/\" href=\"http://121.193.130.41/rs_recruit/\"><span style=\"font-size: 12pt;\"><font color=\"#0563c1\">http://121.193.130.41/rs_recruit/</font></span></a></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px; text-indent: 18pt;\">\r\n	<span style=\"font-size: 12pt;\">截止时间：2014年10月19日</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	&nbsp;<span style=\"font-size: 12pt;\">注：</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;1</span><span style=\"font-size: 12pt;\">、每位应聘者<b>限申报一个岗位</b>，申报多个岗位者，视为无效申请；</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;2</span><span style=\"font-size: 12pt;\">、请应聘者仔细阅读&ldquo;应聘指南&rdquo;，根据应聘步骤进行申报<b>，推荐在附件中上传个人简历</b>；</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;3</span><span style=\"font-size: 12pt;\">、报名截止后，学校将于10月29日之前通知通过初选的应聘者来校笔试。</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	&nbsp;<br />\r\n	&nbsp;<b><span style=\"font-size: 12pt;\">联系信息</span></b></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;联系人：俞霞、朱然、杨真</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;邮&nbsp;箱：&nbsp;recruitment@tju.edu.cn</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;电&nbsp;话：(+)86-022-27409957,(+)86-022-27404886</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;传</span><span style=\"font-size: 12pt;\"><font size=\"+0\">&nbsp;&nbsp;</font></span><span style=\"font-size: 12pt;\">真：(+)86-022-27404177</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	<span style=\"font-size: 12pt;\">&nbsp; &nbsp;地</span><span style=\"font-size: 12pt;\"><font size=\"+0\">&nbsp;&nbsp;</font></span><span style=\"font-size: 12pt;\">址：天津市南开区卫津路92号第九教学楼223室天津大学人事处（300072）</span></p>\r\n<p style=\"color: rgb(1, 68, 138); font-family: Simsun; line-height: 18px;\">\r\n	&nbsp;</p>\r\n', '', '', '127.0.0.1');
