INSERT INTO `cbblab_archives` VALUES ('126', '23', '0', '1416064018', '', '1', '1', '0', '62', '0', '天大牵头APEC可持续能源中心写入“北京纲领”', '', '', 'admin', '未知', '', '1416064018', '1416064058', '1', '天大,牵头,APEC,可,持续,能源,中心,写入,“,', '0', '0', '0', '0', '0', '0', '本站讯（记者王涛 刘晓艳）11月11日，亚太经合组织第二十二次领导人非正式会议落下帷幕。作为会议最重要的成果，会议发表了《北京纲领：构建融合、创新、互联的亚太亚太经合组', '', '1', '0', '0', '10');
INSERT INTO `cbblab_archives` VALUES ('127', '23', '0', '1416064061', '', '1', '1', '0', '66', '0', '天津大学代表团访问欧洲及美洲著名高校和企业', '', '', 'admin', '未知', '', '1416064061', '1416064096', '1', '天津,大学,代表团,访问,欧洲,及,美洲,著名,', '0', '0', '0', '0', '0', '0', '本站讯 （通讯员 马海燕）2014年11月3至12日，校长李家俊率天津大学代表团访问了瑞士苏黎世、巴西圣保罗，以及美国的纽约、辛辛那提和洛杉矶，与各地的世界著名高校和企业进行了', '', '1', '0', '0', '11');
INSERT INTO `cbblab_archives` VALUES ('128', '23', '0', '1416064098', '', '1', '1', '0', '119', '0', '悉尼大学Anthony Welch教授来访天大', '', '', 'admin', '未知', '', '1416064098', '1416064127', '1', '悉尼,大学,Anthony,Welch,教授,来访,天大,本', '0', '0', '0', '0', '0', '0', '本站讯 （通讯员 戴军）11 月 10 日，悉尼大学 Anthony Welch 教授来访天大。元英进副校长在贵宾室会见了 Welch 教授，教育学院、社科处、人事处和国际处代表陪同会见。 元英进副校长首', '', '1', '0', '0', '12');
INSERT INTO `cbblab_archives` VALUES ('129', '23', '0', '1416064139', '', '1', '1', '0', '185', '0', '北洋艺术团赴新校区慰问演出', '', '', 'admin', '未知', '', '1416064139', '1416064166', '1', '北洋,艺术团,赴新,校区,慰问,演出,本站,讯,', '0', '0', '0', '0', '0', '0', '本站讯（记者刘晓艳文并图）2014年11月15日下午，天津大学北洋园新校区繁忙的建设工地上搭建起一座临时舞台。天津大学北洋艺术团的同学们唱起歌来跳起舞，弯下腰鞠躬衷心地向新', '', '1', '0', '0', '13');
