INSERT INTO `cbblab_arctype` VALUES ('22', '1', '0', '112', '通知', '{cmspath}/a/zh/tongzhi', '1', 'index.html', '1', '1', '-1', '0', '0', '{style}/index_article.htm', '{style}/list_article.htm', '{style}/article_article.htm', '{typedir}/{Y}/{M}{D}/{aid}.html', '{typedir}/list_{tid}_{page}.html', 'default', '', '', '', '0', '{cmspath}/a/zh/tongzhi', '', '0', '0', '', '&nbsp;', '');
INSERT INTO `cbblab_arctype` VALUES ('14', '1', '0', '12', '研究队伍', '{cmspath}/a/zh/yanjiuduiwu', '1', 'index.html', '1', '1', '-1', '1', '0', 'default/singlepage.htm', '{style}/list_article.htm', '{style}/article_article.htm', '{typedir}/{Y}/{M}{D}/{aid}.html', '{typedir}/list_{tid}_{page}.html', 'default', '', '', '', '0', '{cmspath}/a/zh/yanjiuduiwu', '', '0', '0', '', '<div>\r\n	<span style=\"font-size:18px;\"><span style=\"color: rgb(51, 51, 51); font-family: 宋体; line-height: 25px;\">&nbsp; &nbsp; 天津大学遥感研究中心团队有副高级以上职称11人，其中，国家千人计划入选者2人，教授4人，副研究员5人。目前团队承担了国家自然科学基金委重点项目、国家海洋局、天津市等多项科研项目。中心作为天津大学交叉学科的创新型科研平台，拥有一个高素质、年轻化、专业化的科研团队。</span></span><br />\r\n	&nbsp;</div>\r\n<div style=\"margin-left:50px\">\r\n	<img alt=\"\" src=\"/uploads/allimg/141115/1-141115221311309.jpg\" style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 18px; line-height: 25px; width: 88px; height: 127px;\" /></div>\r\n<div style=\"width:340px;height:152px;margin-left:154px;margin-top:-174px;\">\r\n	<strong><span style=\"font-size:24px;\">&nbsp;&nbsp; &nbsp;<br />\r\n	&nbsp; &nbsp; &nbsp;<br />\r\n	中心主任</span></strong>： <span style=\"font-size:18px;\">&nbsp;<br />\r\n	&nbsp; &nbsp;周国清：博士，教授，博士生导师，国家千人计划入选者，863计划主题专家，德国洪堡基金学者,主要研究方向为摄影测量与遥感，激光雷达。</span><br />\r\n	<div>\r\n		&nbsp;</div>\r\n</div>\r\n<div>\r\n	<div>\r\n		<img alt=\"\" src=\"/uploads/allimg/141115/1-141115221340E8.jpg\" style=\"width: 101px; height: 127px; margin-left: 500px; margin-top: -103px;\" /></div>\r\n	<div style=\"width:340px;height:127px;margin-left:606px;margin-top:-120px;\">\r\n		<span style=\"font-size:18px;\">&nbsp;<br />\r\n		&nbsp; &nbsp;史伟：博士，教授，博士生导师, 国家千人计划入选者，研究方向为光纤激光技术及太赫兹光子学，高端光纤激光器和光学太赫兹新技术。</span></div>\r\n</div>\r\n<br />\r\n<div>\r\n	<div>\r\n		<img alt=\"\" src=\"/uploads/allimg/141115/1-141115221359E3.jpg\" style=\"width: 88px; margin-left: 50px; margin-top: 0px; height: 127px;\" /></div>\r\n	<div style=\"width:340px;height:127px;margin-left:154px;margin-top:-120px;\">\r\n		<span style=\"font-size:18px;\">&nbsp;<br />\r\n		&nbsp;<span style=\"font-family:arial,helvetica,sans-serif;\"> &nbsp;王向军：博士，教授，博士生导师，研究方向为光电探测及传感技术、计算机视觉及图像测量技术、MEMS及微光机电系统技术。</span></span></div>\r\n</div>\r\n<br />\r\n<div>\r\n	<div>\r\n		<img alt=\"\" src=\"/uploads/allimg/141115/1-141115221413154.jpg\" style=\"width: 101px; height: 127px; margin-left: 500px; margin-top: -158px;\" /></div>\r\n	<div style=\"width:340px;height:127px;margin-left:606px;margin-top:-158px;\">\r\n		<span style=\"font-size:18px;\">&nbsp;<br />\r\n		&nbsp; &nbsp;黄战华：博士，教授，博士生导师，研究方向为光电探测技术及系统, 数字图像应用技术,成像光学与显示,THz波探测与应用技术。</span></div>\r\n</div>\r\n<div style=\"margin-left:50px;\">\r\n	<ul>\r\n		<li>\r\n			<span style=\"font-size:18px;\"><span>罗卿莉：博士，副研究员，研究方向为InSAR应用与城市真正射影像生成；</span></span></li>\r\n		<li>\r\n			<span style=\"font-size:18px;\"><span>张广运：博士，副研究员，研究方向为高光谱与激光雷达融合处理；</span></span></li>\r\n		<li>\r\n			<span style=\"font-size:18px;\"><span>刘剋：博士，副研究员，研究方向为城市环境遥感，高光谱遥感。</span></span></li>\r\n	</ul>\r\n</div>\r\n<br />\r\n', '');
