INSERT INTO `cbblab_arctype` VALUES ('12', '1', '0', '13', '研究方向', '{cmspath}/a/zh/yanjiufangxiang', '1', 'index.html', '1', '1', '-1', '1', '0', 'default/singlepage.htm', '{style}/list_article.htm', '{style}/article_article.htm', '{typedir}/{Y}{M}{D}/{aid}.html', '{typedir}/list_{tid}_{page}.html', 'default', '', '', '研究方向', '0', '{cmspath}/a/zh/yanjiufangxiang', '', '0', '0', '0', '<div id=\"d1\" style=\"margin-left:20px;\">\r\n	<div>\r\n		<span style=\"font-size:18px;\"><strong>研究方向一、先进遥感激光雷达成像仪研制：</strong><br />\r\n		①多元线阵激光雷达成像系统研制；<br />\r\n		②面阵激光雷达成像系统；<br />\r\n		③多光谱、高光谱三维激光成像雷达系统。</span></div>\r\n	<div>\r\n		<img alt=\"\" src=\"/uploads/allimg/141115/1-141115222646237.jpg\" style=\"width: 380px; height: 135px;\" /></div>\r\n</div>\r\n<div id=\"d2\" style=\"margin-top:-235px;margin-left:520px;\">\r\n	<div>\r\n		<strong><span style=\"font-size:18px;\">方向二、遥感大数据智能处理技术：</span></strong><br />\r\n		<span style=\"font-size:18px;\">①高效、快速、稳健的遥感大数据智能挖掘；<br />\r\n		②多源、异构的遥感数据融合；<br />\r\n		③多源遥感数据的反演理论与方法。</span></div>\r\n	<div>\r\n		<img alt=\"\" src=\"/uploads/allimg/141115/1-14111522214A42.jpg\" style=\"width: 396px; height: 134px;\" /></div>\r\n</div>\r\n<div id=\"d3\" style=\"margin-left:20px;margin-top:80px;\">\r\n	<div>\r\n		<strong><span style=\"font-size:18px;\">方向三、遥感环境监测及服务平台：</span></strong><br />\r\n		<span style=\"font-size:18px;\">①海洋生态环境遥感监测；<br />\r\n		②大气环境遥感监测服务平台；<br />\r\n		③城市建筑物真正射影像制作和实景可视化。</span></div>\r\n	<div style=\"margin-left:500px;margin-top:-154px;\">\r\n		<img alt=\"\" src=\"/uploads/allimg/141115/1-141115222234U6.jpg\" style=\"width: 125px; height: 154px;\" /> <img alt=\"\" src=\"/uploads/allimg/141115/1-14111522224Na.jpg\" style=\"width: 177px; height: 122px;\" /></div>\r\n</div>\r\n<br />\r\n', '');
