INSERT INTO `cbblab_arctype` VALUES ('15', '1', '0', '16', '研究动态', '{cmspath}/a/zh/yanjiudongtai', '1', 'index.html', '1', '1', '-1', '0', '0', '{style}/index_article.htm', '{style}/list_article.htm', '{style}/article_article.htm', '{typedir}/{Y}{M}{D}/{aid}.html', '{typedir}/list_{tid}_{page}.html', 'default', '', '', '研究动态', '0', '{cmspath}/a/zh/yanjiudongtai', '', '0', '0', '0', '&nbsp;', '');
INSERT INTO `cbblab_arctype` VALUES ('11', '1', '0', '11', '中心简介', '{cmspath}/a/zh/zhongxinjianjie', '1', 'index.html', '1', '1', '-1', '1', '0', 'default/singlepage.htm', '{style}/list_article.htm', '{style}/article_article.htm', '{typedir}/{Y}/{M}{D}/{aid}.html', '{typedir}/list_{tid}_{page}.html', 'default', '', '', '', '0', '{cmspath}/a/zh/zhongxinjianjie', '', '0', '0', '', '<span style=\"font-size: 16px;\">&nbsp;天津大学&ldquo;遥感研究中心&rdquo;成立于2013年10月。依托天津大学光学工程、仪器科学与技术等优势学科，以精密测试技术及仪器国家重点实验室与光电信息技术教育部重点实验室为平台，针对国家重大战略需求，开展面向遥感交叉学科的前沿技术研究和产业示范应用。<br />\r\n&nbsp;&nbsp;&nbsp; 中心以星空地智能感知为研究定位，重点研究激光电信新材料、新器件和新方法，面向空地海的遥感大数据获取、智能处理和应用服务，以及新一代真正射影像的理论与方法。</span><br />\r\n<br type=\"_moz\" />\r\n', '');
INSERT INTO `cbblab_arctype` VALUES ('19', '1', '0', '18', '联系我们', '{cmspath}/a/zh/lianxiwomen', '1', 'index.html', '1', '1', '-1', '1', '0', 'default/singlepage.htm', '{style}/list_article.htm', '{style}/article_article.htm', '{typedir}/{Y}{M}{D}/{aid}.html', '{typedir}/list_{tid}_{page}.html', 'default', '', '', '联系我们', '0', '{cmspath}/a/zh/lianxiwomen', '', '0', '0', '0', '<h3>\r\n	<br />\r\n	<span style=\"font-size:20px;\"><span style=\"font-family: arial, helvetica, sans-serif;\">电话：022-27407952</span></span></h3>\r\n<h3 style=\"text-align: right;\">\r\n	<span style=\"font-size:20px;\"><img src=\"/uploads/allimg/141115/1-141115222R61Q.jpg\" style=\"margin-right: 10px; float: right; width: 607px; height: 304px; border-width: 0px; border-style: solid; margin-top: -50px; margin-bottom: -50px;\" /></span></h3>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"font-family: arial, helvetica, sans-serif;\">传真：022-27407952</span></span></h3>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"font-family: arial, helvetica, sans-serif;\">邮箱：crs_tj@tju.edu.cn &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></h3>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"font-family: arial, helvetica, sans-serif;\">地址：天津市卫津路92号&nbsp;&nbsp;北洋科学楼一楼</span></span></h3>\r\n<h3>\r\n	<span style=\"font-size:20px;\"><span style=\"font-family: arial, helvetica, sans-serif;\">邮编：30007</span><br />\r\n	<br style=\"font-family: arial, helvetica, sans-serif; font-size: 20px;\" />\r\n	<span style=\"font-family: arial, helvetica, sans-serif;\">网址：http://crs.tju.edu.cn</span></span></h3>\r\n', '');
